        {/* REPORT embaixo */}
        <div className="text-center pb-4">
          <h3 className="font-['Sora',sans-serif] font-bold text-[22px] text-white tracking-[4px] uppercase">
            REPORT
          </h3>
        </div>
      </div>
    </div>
  ];

  return (
    <section className="min-h-screen bg-gradient-to-b from-[#0D0D0D] via-[#191919] to-[#0D0D0D] py-12 px-4 animate-fade-in-up">
      {/* Header Layer */}
      <header className="text-center mb-12">
        <h1 className="font-['Sora',sans-serif] font-extrabold text-[32px] sm:text-[40px] mb-3 bg-gradient-to-r from-[#D4AF37] via-[#F4E5B8] to-[#D4AF37] bg-clip-text text-transparent tracking-tight">
          XTRADERS REPORT 2024
        </h1>
        <p className="font-['Sora',sans-serif] text-[14px] text-[#999] tracking-wide">
          Seu ano em números • Navegue pelos cards em formato stories
        </p>
      </header>

      {/* Main Carousel Stage */}
      <main className="relative mx-auto w-full max-w-[1400px] px-8">
        <Carousel
          opts={{
            align: "center",
            loop: true,
            slidesToScroll: 1,
          }}
          className="w-full"
          setApi={setApi}
        >
          <CarouselContent className={`-ml-0 ${styles.carousel3dContainer}`}>
            {cards.map((card, index) => (
              <CarouselItem key={index} className="pl-0 basis-full">
                <div 
                  ref={(el) => (cardRefs.current[index] = el)}
                  className={`flex justify-center ${styles.carousel3dCard}`}
                >
                  {card}
                </div>
              </CarouselItem>
            ))}
          </CarouselContent>

          {/* Gold pill navigation buttons */}
          <CarouselPrevious className="absolute -left-16 top-1/2 -translate-y-1/2 h-12 w-12 rounded-full border-2 border-[#D4AF37] bg-[#191919] text-[#D4AF37] hover:bg-[#D4AF37] hover:text-[#0D0D0D] transition-all shadow-lg" />
          <CarouselNext className="absolute -right-16 top-1/2 -translate-y-1/2 h-12 w-12 rounded-full border-2 border-[#D4AF37] bg-[#191919] text-[#D4AF37] hover:bg-[#D4AF37] hover:text-[#0D0D0D] transition-all shadow-lg" />
        </Carousel>

        {/* Progress dots - centered with 6px spacing */}
        <div className="flex justify-center items-center gap-1.5 mt-8">
          {Array.from({ length: count }).map((_, idx) => (
            <div
              key={idx}
              className={`rounded-full transition-all ${
                idx === current 
                  ? 'h-2 w-8 bg-[#D4AF37]' 
                  : 'h-2 w-2 bg-[#D4AF37]/30'
              }`}
            />
          ))}
        </div>

        <div className="text-center mt-3">
          <span className="font-['Sora',sans-serif] text-[12px] text-[#666] tracking-wider">
            {current + 1} de {count}
          </span>
        </div>
      </main>

      {/* Footer Layer - Export CTA */}
      <footer className="text-center mt-12">
        <Button
          onClick={() => exportCard(current)}
          className="font-['Sora',sans-serif] font-semibold text-[14px] tracking-wide px-8 py-6 rounded-full bg-gradient-to-r from-[#D4AF37] to-[#B8951C] hover:from-[#B8951C] hover:to-[#D4AF37] text-[#0D0D0D] border-2 border-[#D4AF37]/50 shadow-xl hover:shadow-[#D4AF37]/50 transition-all duration-300"
        >
          <Download className="mr-2 h-5 w-5" />
          Exportar Card Atual
        </Button>
      </footer>
    </section>
  );
}

