              </svg>
              <div className="page-title">
                <h1 className="text-2xl font-regular text-text-primary"></h1>
                <h5 className="text-1xl font-regular text-text-primary"></h5>
              </div>
            </div>
            <button
              id="open-tutorial-button"
              onClick={startTour}
              className="btn btn-secondary !p-2.5 no-print"
              title="Abrir tutorial"
              aria-label="Abrir tutorial da plataforma"
            >
              <HelpCircle className="w-[18px] h-[18px]" strokeWidth={2.5} />
            </button>
          </header>

          <nav className="flex justify-center mb-12 no-print">
            <div id="tour-step-1" className="tab-container" data-tour="tabs">
              <div id="active-tab-slider"></div>
              <button
                id="main-tab-cambial"
                className={`main-tab-button ${activeTab === 'cambial' ? 'active' : ''}`}
                onClick={() => setActiveTab('cambial')}
              >
                Envios e Retiradas
              </button>
              <button
                id="main-tab-ir"
                data-tour="tab-ir"
                className={`main-tab-button ${activeTab === 'ir' ? 'active' : ''}`}
                onClick={() => setActiveTab('ir')}
              >
                Apuração Anual de Resultados
              </button>
              <button
                id="main-tab-wrapped"
                className={`main-tab-button ${activeTab === 'wrapped' ? 'active' : ''}`}
                onClick={() => setActiveTab('wrapped')}
              >
                XTRADERS REPORT
              </button>
            </div>
          </nav>

          <main>
            <div
              id="main-content-cambial"
              className={`main-content ${activeTab === 'cambial' ? '' : 'hidden'}`}
            >
              <CambialTab />
            </div>

            <div
              id="main-content-ir"
              className={`main-content ${activeTab === 'ir' ? '' : 'hidden'}`}
            >
              <IrTab />
            </div>

            <div
              id="main-content-wrapped"
              className={`main-content ${activeTab === 'wrapped' ? '' : 'hidden'}`}
            >
              <WrappedTab />
            </div>
          </main>
        </div>
      </div>

      {/* Auth Container */}
      {user && (
        <div id="auth-container" style={{ display: 'flex' }} className="no-print">
          <span id="user-email" className="text-text-primary text-sm font-medium">
            {user.email}
          </span>
          <button
            id="sign-out-button"
            onClick={() => signOut(auth)}
            className="text-xs text-negative-color hover:text-red transition-colors"
          >
            Sair
          </button>
        </div>
      )}

      {/* Footer */}
      <footer className="fixed bottom-0 left-0 right-0 py-4 bg-gradient-to-t from-[#1A1A1A]/95 to-transparent backdrop-blur-sm">
        <div className="text-center">
          <LogoHeader className="h-6 w-auto mx-auto opacity-50" />
        </div>
      </footer>
    </>
  );
}

export function MainApplication({ user }: MainApplicationProps) {
  return (
    <AppProvider>
      <MainApplicationContent user={user} />
    </AppProvider>
  );
}

