:root {
  --bg-primary: #0d0d0d;
  --surface-card: #1a1a1acc;
  --surface-card-solid: #1a1a1a;
  --border-color: #d4af3733;
  --text-primary: #f0f0f0;
  --text-secondary: #b3b3b3;
  --accent-primary: #d4af37;
  --accent-hover: #e7c668;
  --positive-color: #0aff39;
  --negative-color: #ff0a0a;
  --warning-color: #d4af37;
  --font-size: 16px;
  --background: #0d0d0d;
  --foreground: #f0f0f0;
  --card: #1a1a1acc;
  --card-foreground: #f0f0f0;
  --popover: #1a1a1a;
  --popover-foreground: #f0f0f0;
  --primary: #d4af37;
  --primary-foreground: #0d0d0d;
  --secondary: #1a1a1a;
  --secondary-foreground: #d4af37;
  --muted: #b3b3b3;
  --muted-foreground: #b3b3b3;
  --accent: #d4af37;
  --accent-foreground: #0d0d0d;
  --destructive: #ff0a0a;
  --destructive-foreground: #fff;
  --border: #d4af3733;
  --input: #0d0d0d;
  --input-background: #0d0d0d;
  --switch-background: #cbced4;
  --font-weight-medium: 600;
  --font-weight-normal: 400;
  --ring: #d4af37;
  --radius: .625rem;
  --positive: #0aff39;
  --negative: #ff0a0a;
  --space-xs: .5rem;
  --space-sm: 1rem;
  --space-md: 1.5rem;
  --space-lg: 2.5rem;
  --space-xl: 4rem;
  --border-radius-sm: 8px;
  --border-radius-lg: 20px;
  --shadow-sm: 0 2px 4px #0000004d;
  --shadow-md: 0 5px 15px #0006;
  --shadow-lg: 0 10px 30px #00000080;
  --transition-fluid: all .3s cubic-bezier(.4, 0, .2, 1);
}

html {
  font-size: var(--font-size);
  scroll-behavior: smooth;
}

h1, h2, h3, h4, h5 {
  color: var(--text-primary);
  margin-bottom: var(--space-sm);
  text-shadow: 0 2px 4px #0003;
  letter-spacing: .02em;
  font-weight: 700;
  line-height: 1.3;
  transition: all .5s;
}

h1 {
  text-align: center;
  font-size: 2.5rem;
  line-height: 1.2;
}

h2 {
  text-align: center;
  font-size: 2rem;
  line-height: 1.3;
}

h3 {
  font-size: 1.5rem;
  line-height: 1.4;
}

h4 {
  color: var(--text-secondary);
  font-size: 1.125rem;
  font-weight: 600;
}

h5 {
  letter-spacing: .1em;
  color: var(--accent-primary);
  font-family: Inter, sans-serif;
  font-size: 2rem;
  line-height: 1.4;
}

.text-positive {
  color: var(--positive-color) !important;
}

.text-negative {
  color: var(--negative-color) !important;
}

.text-accent-primary {
  color: var(--accent-primary);
}

.logo-path {
  fill: var(--accent-primary);
}

.content-wrapper {
  z-index: 1;
  padding: var(--space-xl) var(--space-md);
  max-width: 1200px;
  margin: 0 auto;
  position: relative;
  overflow-x: hidden;
}

@keyframes gradient-animation {
  0% {
    background-position: 0%;
  }

  50% {
    background-position: 100%;
  }

  100% {
    background-position: 0%;
  }
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }

  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.stagger-in {
  animation: fadeInUp .5s var(--transition-fluid) both;
  animation-delay: calc(var(--stagger-index) * .1s);
}

.animate-fade-in-up {
  animation: .5s ease-out both fadeInUp;
}

.btn {
  justify-content: center;
  align-items: center;
  gap: var(--space-xs);
  padding: .75rem var(--space-md);
  border-radius: var(--border-radius-sm);
  transition: var(--transition-fluid);
  box-shadow: none;
  cursor: pointer;
  border: 1px solid #0000;
  font-family: Sora, sans-serif;
  font-size: .95rem;
  font-weight: 600;
  display: inline-flex;
  transform: scale(1);
}

.btn:hover {
  box-shadow: var(--shadow-md);
  transform: translateY(-2px);
}

.btn:disabled {
  cursor: not-allowed;
  opacity: .5;
  box-shadow: none;
  transform: none;
}

.btn-primary {
  background: linear-gradient(45deg, var(--accent-hover), #fe9, var(--accent-hover));
  color: #0d0d0d;
  background-size: 150%;
  border-color: #0000;
  transition: all .9s;
}

.btn-primary:active {
  background: linear-gradient(45deg, var(--accent-hover), #fe9, var(--accent-hover));
  color: #0d0d0d;
  background-size: 250%;
  border-color: #0000;
  transition: all .5s;
  transform: scale(.98);
  box-shadow: 0 0 10px #d4af3733;
}

.btn-primary:hover:not(:disabled) {
  animation: 3s infinite gradient-animation;
  box-shadow: 0 0 20px #d4af3766;
}

.btn-secondary {
  color: var(--accent-primary);
  border-color: var(--accent-primary);
  background-color: #0000;
}

.btn-secondary:hover:not(:disabled) {
  background: linear-gradient(45deg, var(--accent-hover), #fe9, var(--accent-hover));
  color: #0d0d0d;
  box-shadow: 0 0 20px #d4af374d;
}

.btn-danger {
  border-color: var(--negative-color);
  color: var(--negative-color);
  background-color: #0000;
}

.btn-danger:hover:not(:disabled) {
  background-color: var(--negative-color);
  color: #fff;
}

.input-field {
  border-radius: var(--border-radius-sm);
  border: 1px solid var(--border-color);
  padding: .75rem var(--space-sm);
  width: 100%;
  transition: var(--transition-fluid);
  color: var(--text-primary);
  box-shadow: none;
  background-color: #0d0d0d;
  font-family: Sora, sans-serif;
  font-size: 1rem;
}

.input-field:hover {
  border-color: var(--accent-primary);
  box-shadow: var(--shadow-sm);
}

.input-field:focus {
  border-color: var(--accent-primary);
  outline: none;
  box-shadow: inset 0 0 10px #d4af3733;
}

.input-field::placeholder {
  color: var(--muted);
}

.password-container {
  position: relative;
}

.password-toggle {
  top: 50%;
  right: var(--space-sm);
  cursor: pointer;
  color: var(--text-secondary);
  background: none;
  border: none;
  padding: 0;
  transition: color .2s;
  position: absolute;
  transform: translateY(-50%);
}

.password-toggle:hover {
  color: var(--text-primary);
}

input[type="date"].input-field {
  background-image: url("data:image/svg+xml;utf8,<svg xmlns=\"http://www.w3.org/2000/svg\" width=\"20\" height=\"20\" viewBox=\"0 0 24 24\" fill=\"none\" stroke=\"%23F0F0F0\" stroke-width=\"2\" stroke-linecap=\"round\" stroke-linejoin=\"round\"><rect x=\"3\" y=\"4\" width=\"18\" height=\"18\" rx=\"2\" ry=\"2\"></rect><line x1=\"16\" y1=\"2\" x2=\"16\" y2=\"6\"></line><line x1=\"8\" y1=\"2\" x2=\"8\" y2=\"6\"></line><line x1=\"3\" y1=\"10\" x2=\"21\" y2=\"10\"></line></svg>");
  background-position: right 1rem center;
  background-repeat: no-repeat;
  padding-right: 2.5rem;
  position: relative;
}

input[type="date"]::-webkit-calendar-picker-indicator {
  opacity: 0;
  cursor: pointer;
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  right: 0;
}

.card {
  background-color: var(--surface-card);
  -webkit-backdrop-filter: blur(12px);
  border-radius: var(--border-radius-lg);
  transition: var(--transition-fluid);
  border: 1px solid var(--border-color);
  padding: 2rem;
  position: relative;
}

.card:hover {
  border-color: #d4af3766;
  transform: translateY(-2px);
}

.glass-card {
  -webkit-backdrop-filter: blur(12px);
  background: #1a1a1acc;
  border: 1px solid #d4af3733;
  border-radius: 20px;
  transition: all .3s cubic-bezier(.4, 0, .2, 1);
}

.glass-card:hover {
  border-color: #d4af3766;
  box-shadow: 0 0 30px #d4af371a;
}

.tab-container {
  gap: var(--space-xs);
  padding: var(--space-xs);
  background: var(--surface-card-solid);
  border: 1px solid var(--border-color);
  border-radius: var(--border-radius-sm);
  display: inline-flex;
  position: relative;
  box-shadow: inset 0 2px 4px #0006;
}

#active-tab-slider {
  top: var(--space-xs);
  height: calc(100% - (var(--space-xs) * 2));
  box-shadow: 0 2px 8px -2px var(--accent-primary);
  z-index: 1;
  background: linear-gradient(45deg, #b3781c, #fe9, #62400c);
  background: linear-gradient(45deg, var(--accent-hover), #fe9, var(--accent-hover));
  color: #0d0d0d;
  background-size: 250%;
  border-color: #0000;
  border-radius: 6px;
  transition: all .5s;
  position: absolute;
  left: 0;
}

.main-tab-button {
  cursor: pointer;
  border-radius: var(--border-radius-sm);
  color: var(--text-secondary);
  z-index: 2;
  background-color: #0000;
  border: 1px solid #0000;
  padding: .6rem 1.25rem;
  font-family: Sora, sans-serif;
  font-size: .9rem;
  font-weight: 600;
  transition: all 1s ease-in-out;
  position: relative;
}

.main-tab-button.active, .main-tab-button.active:hover {
  color: #0d0d0d;
  transition: all 1s ease-in-out;
  animation: 8s infinite gradient-animation;
  box-shadow: 0 0 20px #d4af3766;
}

.hidden {
  display: none !important;
}

.important-note {
  margin-top: var(--space-md);
  padding: var(--space-sm) var(--space-md);
  border-radius: var(--border-radius-sm);
  border: 2px solid var(--border-color);
  border-left: 4px solid var(--warning-color);
  transition: var(--transition-fluid);
  background-color: #00000080;
}

.important-note p:first-child {
  color: var(--warning-color);
  margin: 0;
  font-weight: 600;
}

.important-note p:last-child {
  color: var(--text-secondary);
  opacity: .9;
  margin: 0;
}

.kpi-card {
  background-color: var(--surface-card-solid);
  border-radius: var(--border-radius-sm);
  padding: var(--space-md);
  border: 1px solid var(--border-color);
  box-shadow: var(--shadow-sm);
  transition: var(--transition-fluid);
  text-align: center;
}

.kpi-card:hover {
  border-color: #d4af3780;
  transform: translateY(-2px);
  box-shadow: 0 0 15px #d4af3758;
}

.kpi-card-title {
  color: var(--text-secondary);
  text-transform: uppercase;
  letter-spacing: .05em;
  margin-bottom: var(--space-xs);
  font-size: .8rem;
  font-weight: 600;
}

.kpi-card-value {
  color: var(--text-primary);
  font-size: 1.75rem;
  font-weight: 700;
  line-height: 1.2;
}

.month-card {
  border: 1px solid var(--border-color);
  border-radius: var(--border-radius-sm);
  padding: var(--space-md);
  text-align: center;
  cursor: pointer;
  transition: var(--transition-fluid);
  background-color: var(--surface-card-solid);
  box-shadow: var(--shadow-sm);
}

.month-card:hover {
  border-color: var(--accent-primary);
  box-shadow: var(--shadow-md);
  transform: translateY(-2px);
}

.month-card.profit {
  border-left: 4px solid var(--positive-color);
}

.month-card.loss {
  border-left: 4px solid var(--negative-color);
}

.month-name {
  color: var(--text-primary);
  font-size: 1rem;
  font-weight: 600;
}

.month-result {
  margin-top: var(--space-xs);
  font-size: 1.25rem;
  font-weight: 700;
}

.modal-overlay {
  -webkit-backdrop-filter: blur(10px);
  backdrop-filter: blur(10px);
  z-index: 1500;
  opacity: 0;
  visibility: hidden;
  background-color: #0d0d0dcc;
  justify-content: center;
  align-items: center;
  transition: all .3s ease-in-out;
  display: flex;
  position: fixed;
  inset: 0;
}

.modal-overlay.active {
  opacity: 1;
  visibility: visible;
}

.modal-content {
  background-color: var(--surface-card);
  border: 1px solid var(--border-color);
  padding: var(--space-lg);
  border-radius: var(--border-radius-lg);
  width: 800px;
  max-width: 90%;
  max-height: 90vh;
  box-shadow: var(--shadow-lg);
  flex-direction: column;
  transition: all .3s ease-in-out;
  display: flex;
  overflow-y: auto;
  transform: scale(.95);
}

.modal-overlay.active .modal-content {
  transform: scale(1);
}

.loader {
  border: 4px solid #d4af3733;
  border-top: 4px solid var(--accent-primary);
  border-radius: 50%;
  width: 24px;
  height: 24px;
  animation: 1s linear infinite spin;
}

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

.message {
  margin-top: var(--space-sm);
  border-radius: var(--border-radius-sm);
  padding: .75rem;
  font-size: .9rem;
  font-weight: 500;
  display: block;
}

.message.error {
  color: var(--negative-color);
  background-color: #dc35451a;
  border: 1px solid #dc35454d;
}

.message.success {
  color: var(--positive-color);
  background-color: #28a7451a;
  border: 1px solid #28a7454d;
}

#login-screen, #register-screen, #no-access-screen {
  background-color: var(--bg-primary);
  z-index: 2000;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 100vh;
  display: flex;
  position: fixed;
  top: 0;
  left: 0;
}

.login-box {
  text-align: center;
  width: 100%;
  max-width: 700px;
  animation: fadeInUp .5s var(--transition-fluid) both;
  background-color: var(--surface-card);
  border: 1px solid var(--border-color);
  padding: var(--space-lg);
  border-radius: var(--border-radius-lg);
  box-shadow: var(--shadow-lg);
}

#auth-container {
  top: var(--space-sm);
  right: var(--space-sm);
  z-index: 1001;
  background: var(--surface-card);
  -webkit-backdrop-filter: blur(10px);
  backdrop-filter: blur(10px);
  padding: var(--space-xs) .75rem;
  border-radius: var(--border-radius-sm);
  align-items: center;
  gap: var(--space-sm);
  border: 1px solid var(--border-color);
  box-shadow: var(--shadow-md);
  display: flex;
  position: fixed;
}

#loading-overlay {
  background-color: var(--bg-primary);
  z-index: 1999;
  text-align: center;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 100vh;
  transition: opacity .5s ease-out;
  display: flex;
  position: fixed;
  top: 0;
  left: 0;
}

#tour-overlay {
  z-index: 9998;
  opacity: 0;
  pointer-events: none;
  width: 100%;
  height: 100%;
  transition: opacity .3s ease-out;
  position: fixed;
  top: 0;
  left: 0;
}

#tour-overlay.active {
  opacity: 1;
  pointer-events: all;
}

@keyframes tour-pulse-glow {
  0%, 100% {
    box-shadow: 0 0 0 4px #d4af374d, 0 0 30px 8px #d4af3780, inset 0 0 30px #d4af3726;
  }

  50% {
    box-shadow: 0 0 0 4px #d4af3766, 0 0 40px 12px #d4af3799, inset 0 0 40px #d4af3733;
  }
}

.tour-highlight {
  pointer-events: none;
  transition: all .5s cubic-bezier(.4, 0, .2, 1);
  animation: 2s ease-in-out infinite tour-pulse-glow;
}

.tour-tooltip {
  z-index: 9999;
  background: var(--surface-card-solid);
  border-radius: var(--border-radius-lg);
  border: 1px solid var(--border-color);
  width: 320px;
  padding: var(--space-md);
  animation: fadeInUp .4s var(--transition-fluid) both;
  box-shadow: var(--shadow-lg);
  position: absolute;
}

.tour-nav {
  border-top: 1px solid var(--border-color);
  margin-top: var(--space-md);
  padding-top: var(--space-sm);
  justify-content: space-between;
  align-items: center;
  display: flex;
}

#terms-modal-overlay {
  -webkit-backdrop-filter: blur(8px);
  backdrop-filter: blur(8px);
  z-index: 2500;
  background-color: #0d0d0dcc;
  justify-content: center;
  align-items: center;
  display: none;
  position: fixed;
  inset: 0;
}

.terms-text-container {
  background-color: var(--bg-primary);
  border: 1px solid var(--border-color);
  max-height: 55vh;
  padding: var(--space-md);
  border-radius: var(--border-radius-sm);
  margin-bottom: var(--space-md);
  color: var(--text-secondary);
  line-height: 1.7;
  overflow-y: auto;
}

.terms-text-container p {
  margin-bottom: var(--space-sm);
}

.terms-text-container strong {
  color: var(--text-primary);
}

.terms-text-container h4 {
  color: var(--accent-primary);
  text-align: center;
  margin-bottom: var(--space-md);
  font-size: 1.25rem;
}

.custom-checkbox-label {
  cursor: pointer;
  color: var(--text-primary);
  align-items: center;
  font-size: 1rem;
  display: flex;
}

.custom-checkbox {
  appearance: none;
  border: 2px solid var(--border-color);
  width: 1.25rem;
  height: 1.25rem;
  margin-right: var(--space-xs);
  transition: var(--transition-fluid);
  background-color: var(--surface-card-solid);
  border-radius: 4px;
  position: relative;
}

.custom-checkbox:checked {
  background-color: var(--accent-primary);
  border-color: var(--accent-primary);
}

.custom-checkbox:checked:after {
  content: "✓";
  color: #0d0d0d;
  font-size: 1rem;
  font-weight: bold;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

canvas {
  filter: brightness(1.1);
}

table {
  border-collapse: collapse;
  width: 100%;
}

table th {
  border-bottom: 1px solid var(--border-color);
  text-align: left;
  text-transform: uppercase;
  letter-spacing: .07em;
  color: var(--text-secondary);
  padding: 1rem .75rem;
  font-size: .8rem;
  font-weight: 600;
}

table td {
  color: var(--text-primary);
  border-bottom: 1px solid #d4af371a;
  padding: 1.25rem .75rem;
}

table tbody tr:last-child td {
  border-bottom: none;
}

table tbody tr:hover td {
  background-color: #1a1a1a80;
}

table th:last-child, table td:last-child {
  text-align: right;
}

table th.text-center, table td.text-center {
  text-align: center;
}

.table-icon-button {
  cursor: pointer;
  color: var(--text-secondary);
  transition: var(--transition-fluid);
  background: none;
  border: none;
  border-radius: 50%;
  padding: .25rem;
}

.table-icon-button:hover {
  color: var(--accent-primary);
  background-color: #d4af3726;
  transform: scale(1.1);
}

::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: #1a1a1a80;
}

::-webkit-scrollbar-thumb {
  background: #d4af374d;
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: #d4af3780;
}

.wrapped-container {
  background: none;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  width: 100%;
  min-height: 100vh;
  padding: .5rem 0 1rem;
  display: flex;
  position: relative;
  overflow-x: hidden;
}

.wrapped-title-main {
  color: var(--text-primary);
  text-align: center;
  letter-spacing: -.02em;
  margin-top: .5rem;
  margin-bottom: .25rem;
  font-size: 2rem;
  font-weight: 700;
}

.wrapped-subtitle-main {
  color: var(--text-secondary);
  text-align: center;
  opacity: .8;
  margin-bottom: 4rem;
  font-size: 1rem;
  font-weight: 400;
}

.mySwiper {
  width: 100%;
  max-width: 100%;
  min-height: 720px;
  margin-bottom: 60px;
  padding: 20px 0 30px;
  position: relative;
}

.mySwiper .swiper-wrapper {
  align-items: center;
}

.mySwiper .swiper-slide {
  background: var(--surface-card);
  -webkit-backdrop-filter: blur(12px);
  backdrop-filter: blur(12px);
  border: 1px solid var(--border-color);
  opacity: .6;
  backface-visibility: hidden;
  border-radius: 16px;
  flex-direction: column;
  width: 580px;
  height: 750px;
  transition: all .4s;
  display: flex;
  overflow: hidden;
  transform: translateZ(0);
  box-shadow: 0 8px 32px #0003;
}

.mySwiper .swiper-slide-active {
  opacity: 1;
  border-color: var(--accent-primary);
  z-index: 10;
  box-shadow: 0 16px 48px #d4af3726;
}

.wrapped-slide {
  background: var(--surface-card);
}

.slide-content {
  flex-direction: column;
  justify-content: flex-start;
  gap: 1rem;
  width: 100%;
  height: 100%;
  padding: 2rem 1.8rem;
  display: flex;
  position: relative;
}

.slide-title {
  color: var(--text-primary);
  text-transform: uppercase;
  letter-spacing: .1em;
  text-align: center;
  flex-shrink: 0;
  margin-bottom: 1rem;
  font-size: 1.2rem;
  font-weight: 600;
}

.wrapped-slide-intro-v2 {
  background: #191919;
  border: 1px solid #9c7b0e;
  border-radius: 43px;
  padding: 0;
}

.intro-v2-content {
  width: 100%;
  height: 100%;
  position: relative;
}

.intro-v2-header {
  color: #e0e0e0;
  opacity: 1;
  letter-spacing: .1em;
  text-transform: uppercase;
  font-size: 2.5rem;
  font-weight: 600;
  position: absolute;
  top: 4rem;
  left: 50%;
  transform: translateX(-50%);
}

.intro-v2-footer {
  color: #e0e0e0;
  opacity: 1;
  letter-spacing: .1em;
  text-transform: uppercase;
  font-family: Montserrat, sans-serif;
  font-size: 3rem;
  font-weight: bolder;
  position: absolute;
  bottom: 4rem;
  left: 50%;
  transform: translateX(-50%);
}

.intro-v2-logo {
  position: absolute;
  top: 29%;
  left: 16%;
}

.intro-v2-logo svg {
  filter: drop-shadow(0 0 30px #d4af3766);
  width: 400px;
  height: auto;
}

.main-result-card {
  border: 1px solid var(--accent-primary);
  text-align: center;
  background: #d4af3714;
  border-radius: 12px;
  flex-shrink: 0;
  margin-bottom: 1.5rem;
  padding: 1.5rem;
}

.result-label {
  color: var(--accent-primary);
  text-transform: uppercase;
  letter-spacing: .1em;
  margin-bottom: .4rem;
  font-size: .8rem;
  font-weight: 600;
}

.result-value {
  color: var(--text-primary);
  font-size: 1.8rem;
  font-weight: 800;
  line-height: 1.2;
}

.metrics-grid {
  flex: 1;
  grid-template-columns: 1fr 1fr;
  gap: .8rem;
  width: 100%;
  display: grid;
}

.metric-card {
  text-align: center;
  min-height: 65px;
  transition: var(--transition-fluid);
  background: #ffffff05;
  border: 1px solid #d4af371a;
  border-radius: 8px;
  flex-direction: column;
  justify-content: center;
  padding: .8rem .6rem;
  display: flex;
}

.metric-card:hover {
  border-color: var(--accent-primary);
  transform: translateY(-1px);
}

.metric-label {
  color: var(--text-secondary);
  text-transform: uppercase;
  letter-spacing: .05em;
  margin-bottom: .3rem;
  font-size: .65rem;
  font-weight: 500;
  line-height: 1.1;
}

.metric-value {
  color: var(--text-primary);
  font-size: 1.5rem;
  font-weight: 700;
  line-height: 1.1;
}

.assets-ranking {
  flex-direction: column;
  gap: 1.5rem;
  width: 100%;
  height: 100px;
  margin-top: 28%;
  display: flex;
}

.asset-card {
  border-radius: var(--border-radius-sm);
  border-left: 3px solid var(--accent-primary);
  background: #0003;
  justify-content: space-between;
  align-items: center;
  height: 80px;
  padding: .8rem 2.2rem;
  display: flex;
}

.asset-rank-info {
  align-items: center;
  gap: 1rem;
  display: flex;
}

.asset-rank {
  color: var(--text-secondary);
  font-size: 1.8rem;
  font-weight: 700;
}

.asset-name {
  color: var(--text-primary);
  text-align: left;
  font-weight: 600;
}

.asset-profit {
  color: var(--accent-primary);
  font-weight: 600;
}

.chart-container {
  background: #ffffff05;
  border: 1px solid #d4af371a;
  border-radius: 8px;
  flex-shrink: 0;
  width: 100%;
  height: 220px;
  margin-top: 120px;
  margin-bottom: 1.2rem;
  padding: .4rem;
}

.month-summary {
  flex-shrink: 0;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
  width: 100%;
  display: grid;
}

.month-label {
  color: var(--text-secondary);
  margin-bottom: .4rem;
  font-size: .7rem;
  font-weight: 500;
  line-height: 1.1;
}

.month-value {
  color: var(--text-primary);
  font-size: 1.3rem;
  font-weight: 700;
  line-height: 1.1;
}

.monthly-assets-grid {
  grid-template-columns: repeat(auto-fill, minmax(150px, 1fr));
  align-content: center;
  gap: .5rem;
  width: 100%;
  margin-top: 40px;
  display: grid;
  overflow-y: hidden;
}

.monthly-asset-card {
  border-radius: var(--border-radius-sm);
  text-align: center;
  box-sizing: border-box;
  background: #0003;
  border: 1px solid #d4af371a;
  flex-direction: column;
  justify-content: center;
  min-height: 70px;
  padding: .8rem .5rem;
  display: flex;
}

.best-asset {
  color: var(--text-primary);
  margin: .25rem 0;
  font-size: .9rem;
  font-weight: 600;
}

.final-summary-card {
  border-radius: var(--border-radius-lg);
  border: 1px solid var(--border-color);
  background: #1c1c1c;
  flex-direction: column;
  gap: 1.2rem;
  width: 100%;
  max-width: 420px;
  max-height: 610px;
  margin: auto;
  padding: 2.5rem 2rem;
  display: flex;
  box-shadow: 0 16px 48px #0006;
}

.final-card-main-metric {
  text-align: center;
  flex-direction: column;
  justify-content: center;
  display: flex;
}

.main-metric-label {
  color: var(--accent-primary);
  font-size: 1rem;
  font-weight: 700;
}

.main-metric-value {
  color: var(--text-primary);
  font-size: 2.75rem;
  font-weight: 700;
  line-height: 1.1;
}

.final-card-secondary-grid {
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
  display: grid;
}

.secondary-metric-card {
  border-radius: var(--border-radius-sm);
  text-align: center;
  background: #0003;
  border: 1px solid #0000;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-height: 95px;
  padding: 1rem;
  display: flex;
}

.secondary-metric-card.positive {
  border-color: var(--positive-color);
}

.secondary-metric-label {
  color: var(--text-secondary);
  text-transform: uppercase;
  margin-bottom: .5rem;
  font-size: .75rem;
  font-weight: 500;
  line-height: 1.2;
}

.secondary-metric-value {
  color: var(--text-primary);
  font-size: 1.5rem;
  font-weight: 600;
  line-height: 1.1;
}

.final-card-top-asset {
  border-radius: var(--border-radius-sm);
  border-left: 3px solid var(--accent-primary);
  background: #0003;
  justify-content: space-between;
  align-items: center;
  padding: 1.1rem 1.2rem;
  display: flex;
}

.top-asset-info {
  align-items: center;
  gap: .75rem;
  display: flex;
}

.top-asset-rank {
  color: var(--accent-primary);
  font-size: 1.1rem;
  font-weight: 700;
}

.top-asset-name {
  color: var(--text-primary);
  text-align: left;
  font-weight: 600;
}

.top-asset-profit-value {
  color: var(--text-primary);
  font-size: 1.1rem;
  font-weight: 600;
}

.final-card-chart-container {
  border-radius: var(--border-radius-sm);
  background: #0003;
  width: 100%;
  height: 180px;
  padding: 1rem;
}

.action-buttons {
  justify-content: center;
  gap: 1rem;
  margin-top: 2rem;
  display: flex;
}

.final-summary-card, .export-summary-card, .final-card-main-metric, .final-card-secondary-grid, .secondary-metric-card, .final-card-top-asset, .final-card-chart-container {
  background-color: #1c1c1c !important;
  background-image: none !important;
}

.secondary-metric-card, .final-card-top-asset, .final-card-chart-container {
  background-color: #0003 !important;
}

@media print {
  .no-print {
    display: none !important;
  }

  body {
    color: #000 !important;
    background: #fff !important;
  }

  .glass-card {
    background: #fff !important;
    border: 1px solid #ccc !important;
  }
}

@media (width <= 600px) {
  .content-wrapper {
    padding: var(--space-lg) var(--space-sm);
  }

  h1 {
    font-size: 2rem;
  }

  h2 {
    font-size: 1.5rem;
  }

  .card {
    padding: var(--space-md);
  }

  .kpi-card {
    padding: var(--space-sm);
  }

  .kpi-card-value {
    font-size: 1.5rem;
  }

  .modal-content {
    width: 90%;
    padding: var(--space-md);
  }

  .tab-container {
    gap: var(--space-xs);
    padding: var(--space-xs);
  }

  .main-tab-button {
    padding: .5rem 1rem;
    font-size: .85rem;
  }

  .login-box {
    max-width: 90%;
    padding: var(--space-md);
  }

  #auth-container {
    top: var(--space-xs);
    right: var(--space-xs);
    padding: var(--space-xs) .5rem;
  }

  #auth-container span {
    display: none;
  }

  #loading-overlay {
    padding: var(--space-md);
  }

  .month-card {
    padding: var(--space-sm);
  }

  .month-name {
    font-size: .9rem;
  }

  .month-result {
    font-size: 1.1rem;
  }

  .kpi-card-title {
    font-size: .75rem;
  }

  .terms-text-container {
    max-height: 50vh;
  }

  .modal-overlay {
    padding: var(--space-sm);
  }

  .mySwiper .swiper-slide {
    width: 320px;
    height: 600px;
  }

  .slide-content {
    padding: 1.5rem 1.2rem;
  }

  .monthly-assets-grid, .metrics-grid {
    grid-template-columns: 1fr;
    gap: .75rem;
  }
}

